<style>
.notifier {
    position: relative;
    display: inline-block;
}

.bell {
    font-size: 17px;
    color: #FFF;
    transition: 0.3s;
}

.bell:hover {
    color: #EF476F;
}

.badge2 {
    position: absolute;
    top: -5px;
    left: 11px;
    padding: 4px 4px;
    line-height: 10px;
    height: 18px;
    background: #EF476F;
    color: #FFF;
    border-radius: 10px;
    white-space: nowrap;
    display: inline-block;
    /* padding: 0.35em 0.65em; */
    font-size: 0.7em;
    font-weight: 700;
    /* line-height: 1; */
    /* color: #fff; */
    text-align: center;
    /* white-space: nowrap; */
    vertical-align: baseline;
    /* border-radius: 0.25rem; */
}


.notifier.new .badge2 {
    animation: pulse 2s ease-out;
    animation-iteration-count: infinite;
}

@keyframes pulse {
    40% {
        transform: scale3d(1, 1, 1);
    }

    50% {
        transform: scale3d(1.3, 1.3, 1.3);
    }

    55% {
        transform: scale3d(1, 1, 1);
    }

    60% {
        transform: scale3d(1.3, 1.3, 1.3);
    }

    65% {
        transform: scale3d(1, 1, 1);
    }
}
</style>
